						op[0] = ClampToUQ15(c0 + dif);
						op[1] = ClampToUQ15(c1 + dif);
						op[2] = ClampToUQ15(c2 + dif);
					}
					else
					{
						op[0] = c0;
						op[1] = c1;
						op[2] = c2;
